function out = pikseli_coder(im1, im2, bitnum)mask = uint8(2 ^ bitnum - 1);#figure, imshow(im1);imkodorig = bitand(im1,bitcmp(mask));#figure, imshow(imkodorig);imkodskriveno = reverse_bits_matrix(bitand(im2, reverse_bits8(mask)));#figure, imshow(imkodskriveno);#figure, imshow(bitand(im2, reverse_bits8(mask)));out = imkodorig + imkodskriveno;#figure, imshow(out);endfunction